import React, { useState } from "react";

export default function App() {
  const [name1, setName1] = useState("");
  const [name2, setName2] = useState("");
  const [result, setResult] = useState("");

  const flamesLogic = (n1, n2) => {
    let nameA = n1.toLowerCase().replace(/ /g, "");
    let nameB = n2.toLowerCase().replace(/ /g, "");
    for (let char of n1) {
      if (nameB.includes(char)) {
        nameA = nameA.replace(char, "");
        nameB = nameB.replace(char, "");
      }
    }
    let count = (nameA + nameB).length;
    let flames = ["Friends", "Love", "Affection", "Marriage", "Enemy", "Siblings"];
    let idx = 0;
    while (flames.length > 1) {
      idx = (idx + count - 1) % flames.length;
      flames.splice(idx, 1);
    }
    return flames[0];
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name1 && name2) {
      setResult(flamesLogic(name1, name2));
    }
  };

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;600&display=swap');

        body {
          margin: 0;
          font-family: 'Poppins', sans-serif;
          background: linear-gradient(135deg, #ff7eb3, #ff758c);
          height: 100vh;
          overflow: hidden;
        }

        .container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100%;
          position: relative;
        }

        .card {
          background: rgba(255, 255, 255, 0.15);
          backdrop-filter: blur(10px);
          padding: 2rem;
          border-radius: 20px;
          box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
          text-align: center;
          max-width: 350px;
          width: 100%;
        }

        h1 {
          color: white;
          margin-bottom: 1rem;
        }

        input {
          width: 100%;
          padding: 10px;
          margin: 0.5rem 0;
          border: none;
          border-radius: 10px;
          font-size: 1rem;
        }

        button {
          width: 100%;
          padding: 12px;
          background: linear-gradient(90deg, #ff4e50, #f9d423);
          border: none;
          border-radius: 10px;
          font-size: 1rem;
          font-weight: bold;
          color: white;
          cursor: pointer;
          margin-top: 0.5rem;
          transition: transform 0.2s;
        }

        button:hover {
          transform: scale(1.05);
        }

        .result {
          margin-top: 1rem;
          font-size: 1.2rem;
          color: white;
          font-weight: bold;
        }

        /* Floating Hearts Animation */
        .hearts::before,
        .hearts::after {
          content: "üíñ";
          position: absolute;
          animation: float 8s infinite ease-in-out;
          font-size: 2rem;
        }

        .hearts::after {
          content: "‚ù§Ô∏è";
          left: 50%;
          animation-delay: 4s;
        }

        @keyframes float {
          0% {
            transform: translateY(100vh) scale(0.5);
            opacity: 0.3;
          }
          50% {
            opacity: 1;
          }
          100% {
            transform: translateY(-10vh) scale(1.2);
            opacity: 0;
          }
        }
      `}</style>

      <div className="container">
        <div className="hearts"></div>
        <div className="card">
          <h1>üíñ FLAMES Game</h1>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Your Name"
              value={name1}
              onChange={(e) => setName1(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Crush's Name"
              value={name2}
              onChange={(e) => setName2(e.target.value)}
              required
            />
            <button type="submit">Find Result</button>
          </form>
          {result && <div className="result">Result: {result}</div>}
        </div>
      </div>
    </>
  );
}
